// â”€â”€ Language switcher logic â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
(function() {
  const STORAGE_KEY = 'adam_lang';
  let currentLang = localStorage.getItem(STORAGE_KEY) || 'de';

  function applyLang(lang) {
    const t = ADAM_TRANSLATIONS[lang] || ADAM_TRANSLATIONS['de'];
    currentLang = lang;
    localStorage.setItem(STORAGE_KEY, lang);

    // RTL support
    document.documentElement.dir = t.dir || 'ltr';

    // Apply all data-i18n elements
    document.querySelectorAll('[data-i18n]').forEach(el => {
      const key = el.getAttribute('data-i18n');
      if (t[key]) el.textContent = t[key];
    });

    // Update placeholder attrs
    document.querySelectorAll('[data-i18n-placeholder]').forEach(el => {
      const key = el.getAttribute('data-i18n-placeholder');
      if (t[key]) el.placeholder = t[key];
    });

    // Update switcher button
    const btn = document.getElementById('lang-btn');
    if (btn) btn.textContent = t.flag + ' ' + t.lang;

    // Close dropdown
    const dd = document.getElementById('lang-dropdown');
    if (dd) dd.classList.remove('open');
  }

  function buildSwitcher() {
    const container = document.createElement('div');
    container.className = 'lang-switcher';
    container.innerHTML = `
      <button id="lang-btn" class="lang-btn" onclick="document.getElementById('lang-dropdown').classList.toggle('open')">ðŸ‡©ðŸ‡ª Deutsch</button>
      <div id="lang-dropdown" class="lang-dropdown">
        ${Object.entries(ADAM_TRANSLATIONS).map(([code, t]) =>
          `<button class="lang-option" onclick="window.adamSetLang('${code}')">${t.flag} ${t.lang}</button>`
        ).join('')}
      </div>
    `;
    // Insert in nav
    const nav = document.querySelector('nav') || document.querySelector('.navbar');
    if (nav) nav.appendChild(container);
  }

  window.adamSetLang = applyLang;

  document.addEventListener('DOMContentLoaded', () => {
    // buildSwitcher(); // Nav-Switcher deaktiviert
    applyLang(currentLang);
  });

  // Close dropdown on outside click
  document.addEventListener('click', (e) => {
    if (!e.target.closest('.lang-switcher')) {
      const dd = document.getElementById('lang-dropdown');
      if (dd) dd.classList.remove('open');
    }
  });
})();
